import Square from "./Square";
import { useState } from 'react';

export default function Board() {
    const m=2, n=3;
    const numConsecutiveToWin=2;
    const [squares, setSquares] = useState(Array(m*n).fill(null));
    const [xIsNext, setXIsNext] = useState(true);
    const [latestPos, setLatestPos] = useState(null);

    function handleClick(i) {
      if (squares[i] || calculateWinner(squares)) {
        return;
      }
      const nextSquares = squares.slice();
      if (xIsNext) {
        nextSquares[i] = "X";
      } else {
        nextSquares[i] = "O";
      }
      setSquares(nextSquares);
      setXIsNext(!xIsNext);
      setLatestPos(i);
    }
    function posToRowCol(pos){
        return [parseInt(pos/n), pos %n];
    }
    function rowColToPos(row, col){
        return row*n+col;
    }
    function calculateWinner(){
        const [row, col] = posToRowCol(latestPos);
        const latestSymbol = xIsNext? "O": "X";
        console.log(`Current pos is ${latestPos}, row is ${row} col is ${col}, latest symbol is ${latestSymbol}`);

        // check vertical
        var leftRow=row, rightRow=row;
        while ((leftRow>=0)&(squares[rowColToPos(leftRow, col)]===latestSymbol)) {leftRow--;}
        while ((rightRow<=n-1)&(squares[rowColToPos(rightRow, col)]===latestSymbol)) {rightRow++;}
        leftRow = Math.max(0, leftRow);
        rightRow = Math.min(n-1, rightRow);
        console.log(`leftrow=${leftRow}, rightrow=${rightRow}`);
        if (rightRow - leftRow>=numConsecutiveToWin){return latestSymbol;}

        // check horizontal
        var upperCol=col, lowerCol=col;
        while ((upperCol>=0)&(squares[rowColToPos(row, upperCol)]===latestSymbol)) {upperCol--;}
        while ((lowerCol<=m-1)&(squares[rowColToPos(row, lowerCol)]===latestSymbol)) {lowerCol++;}
        upperCol = Math.max(0, upperCol);
        lowerCol = Math.min(m-1, lowerCol);
        if (lowerCol - upperCol>=numConsecutiveToWin){return latestSymbol;}

        // checking upright diagonal        
        leftRow=row;
        rightRow=row;
        upperCol=col;
        lowerCol=col;
        while (squares[rowColToPos(row+diagonalMoveUp, col+diagonalMoveUp)]===latestSymbol) {diagonalMoveUp++;}
        while (squares[rowColToPos(row+diagonalMoveDown, col+diagonalMoveDown)]===latestSymbol) {diagonalMoveDown--;}
        if (diagonalMoveUp + diagonalMoveDown>=numConsecutiveToWin) {return latestSymbol;}
        
        // check upleft diagonal
        diagonalMoveUp=0; 
        diagonalMoveDown=0;
        while (squares[rowColToPos(row+diagonalMoveUp, col-diagonalMoveUp)]===latestSymbol) {diagonalMoveUp++;}
        while (squares[rowColToPos(row+diagonalMoveDown, col-diagonalMoveDown)]===latestSymbol) {diagonalMoveDown--;}
        if (diagonalMoveUp + diagonalMoveDown>=numConsecutiveToWin) {return latestSymbol;}
        return null;
    }

    const winner = calculateWinner();
    // const winner = null; //TODO
    let status;
    if (winner) {
      status = "Winner: " + winner;
    } else {
      status = "Next player: " + (xIsNext ? "X" : "O");
    }

    const board = []
    for (let row = 0; row < m; row ++) {
        let board_row = [];
        for (let col = 0; col < n; col ++) {
            board_row.push(<Square value={squares[row*n + col]} onSquareClick={()=>handleClick(row*n + col)}/>)
        }
        board.push(<div className="board-row">{board_row}</div>);
    }
    return (
      <>
        <div className="status">{status};</div>
        {board}
      </>
    );
  }
